// ----------- Markdown Inline ë³€í™˜ í•¨ìˆ˜ ----------- //
function mdToRichText(text) {
  const patterns = [
    { regex: /\*\*(.*?)\*\*/, annotation: { bold: true } },
    { regex: /\*(.*?)\*/, annotation: { italic: true } },
    { regex: /==(.*?)==/, annotation: { color: "yellow_background" } }
  ];
  let rich = [];
  let rest = text;
  while (rest.length > 0) {
    let minIdx = rest.length;
    let matchObj = null;
    let patternObj = null;
    for (const p of patterns) {
      const m = p.regex.exec(rest);
      if (m && m.index < minIdx) {
        minIdx = m.index;
        matchObj = m;
        patternObj = p;
      }
    }
    if (matchObj) {
      if (matchObj.index > 0) {
        rich.push({ type: 'text', text: { content: rest.slice(0, matchObj.index) } });
      }
      rich.push({
        type: 'text',
        text: { content: matchObj[1] },
        annotations: patternObj.annotation
      });
      rest = rest.slice(matchObj.index + matchObj[0].length);
    } else {
      if (rest.length > 0) rich.push({ type: 'text', text: { content: rest } });
      break;
    }
  }
  return rich;
}

// ----------- 2ë‹¨ê³„ childrenë§Œ í—ˆìš©í•˜ë„ë¡ flatten ----------- //
function safeChildren(children) {
  if (!children) return;
  return children.map(child => {
    if (['bulleted_list_item', 'numbered_list_item'].includes(child.type)) {
      if (child[child.type].children) {
        child[child.type].children = child[child.type].children.map(grandChild => {
          if (grandChild[grandChild.type] && grandChild[grandChild.type].children) {
            delete grandChild[grandChild.type].children;
          }
          return grandChild;
        });
      }
    }
    return child;
  });
}

// ----------- ë¶ˆë¦¿ì— ìŒë”°ì˜´í‘œ ì—¬ëŸ¬ ê°œ â†’ children ë¶„ë¦¬ ----------- //
function splitBulletsWithQuotes(text) {
  const quotePattern = /"([^"]+)"/g;
  let matches = [];
  let match;
  let lastIndex = 0;
  let children = [];
  while ((match = quotePattern.exec(text)) !== null) {
    matches.push(match[1]);
    lastIndex = quotePattern.lastIndex;
  }
  if (matches.length > 1) {
    for (let quoted of matches) {
      children.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: { rich_text: mdToRichText(quoted) }
      });
    }
    let rest = text.slice(lastIndex).replace(/^[,\s]+/, '');
    if (rest.length > 0) {
      children.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: { rich_text: mdToRichText(rest) }
      });
    }
    return children;
  }
  return null;
}

