// SerpAPI 응답을 Notion 형식으로 변환 (중복 제거 + 네이트 필터링)

// Notion Trigger에서 Movie와 keywordPageId 가져오기
const movie = $('Notion Trigger').item.json.properties.Movie.title[0].plain_text;
const keywordPageId = $('Notion Trigger').item.json.id;

// 4개 키워드의 SerpAPI 결과 모두 받기
const allSerpResults = $input.all();
const splitOutItems = $('Split Out').all();

// 차단할 도메인 리스트
const blockDomains = [
  'news.nate.com',
  'v.daum.net'
];

// 중복 제거용
const seenUrls = new Set();
const allTransformedNews = [];
const collectedDateTime = new Date();

// 통계
let blockedCount = 0;

// 각 키워드별 처리
allSerpResults.forEach((serpItem, index) => {
  const serpResult = serpItem.json;

  // 현재 키워드 (Split Out에서 전달됨)
  const currentKeyword = splitOutItems[index].json.Keywords;

  // 뉴스 결과 확인
  if (!serpResult.news_results || serpResult.news_results.length === 0) {
    console.log(`"${currentKeyword}" - 뉴스 없음`);
    return;
  }

  // 모든 뉴스 추출 (stories 배열 펼치기)
  const allNews = [];
  let globalPosition = 0;

  serpResult.news_results.forEach((group, groupIndex) => {
    // stories 배열이 있는 경우
    if (group.stories && Array.isArray(group.stories)) {
      group.stories.forEach((story) => {
        globalPosition++;
        allNews.push({
          ...story,
          position: globalPosition
        });
      });
    } 
    // stories 없이 단독 뉴스인 경우
    else if (group.title) {
      globalPosition++;
      allNews.push({
        ...group,
        position: globalPosition
      });
    }
  });

  console.log(`"${currentKeyword}" - ${allNews.length}개 뉴스 추출`);

  // 각 뉴스 변환
  allNews.forEach((news) => {
    const url = news.link;

    // ============ 네이트/다음 필터링 ============
    if (url && blockDomains.some(domain => url.includes(domain))) {
      blockedCount++;
      return;  // 건너뛰기
    }
    // ==========================================

    // 중복 체크
    if (!url || seenUrls.has(url)) return;
    seenUrls.add(url);

    // 날짜 정규화
    let publishedDateOnly = null;

    if (news.date) {
      try {
        const dateStr = news.date.replace(/, \+\d{4} UTC$/, '');
        const parsedDate = new Date(dateStr);
        publishedDateOnly = parsedDate.toISOString().split('T')[0];
      } catch (e) {
        console.log('날짜 파싱 실패:', news.date);
        publishedDateOnly = new Date().toISOString().split('T')[0];
      }
    } else {
      publishedDateOnly = new Date().toISOString().split('T')[0];
    }

    // 언론사 추출
    const sourceName = news.source?.name || 'Unknown';

    // 카테고리 자동 분류
    const searchText = `${news.title} ${sourceName}`.toLowerCase();
    let category = '기타';

    if (/문화|엔터|entertainment|영화|음악|예능|드라마/.test(searchText)) category = '문화';
    else if (/경제|finance|증시|주식|비즈니스/.test(searchText)) category = '경제';
    else if (/기술|tech|ai|인공지능|it/.test(searchText)) category = '기술';
    else if (/정치|politics|election|선거/.test(searchText)) category = '정치';
    else if (/스포츠|sports|축구|야구/.test(searchText)) category = '스포츠';

    allTransformedNews.push({
      title: news.title || 'Untitled',
      url: url,
      source: sourceName,
      thumbnail: news.thumbnail || news.thumbnail_small || null,
      publishedDateOnly: publishedDateOnly,
      atTime: collectedDateTime.toISOString(),
      position: news.position,
      category: category,
      keyword: currentKeyword,
      movie: movie,
      keywordPageId: keywordPageId
    });
  });
});

console.log(`최종: ${allTransformedNews.length}개 뉴스 (중복 제거 완료)`);
console.log(`차단: ${blockedCount}개 (네이트/다음)`);

return allTransformedNews.map(item => ({ json: item }));

// 1. 현재 노드(Filter 또는 Split Out 이후)에서 데이터 받기
const items = $input.all();

// 2. Transform Data에서 원본 메타데이터 가져오기
const transformData = $('Transform Data').all();

console.log(`현재 아이템 수: ${items.length}`);
console.log(`Transform Data 수: ${transformData.length}`);

// 3. 본문 정제 함수
function cleanNewsBody(rawContent) {
  if (!rawContent) return '[본문 없음]';
  if (Array.isArray(rawContent)) rawContent = rawContent.join('\n');
  else if (typeof rawContent === 'object' && rawContent !== null) {
    rawContent = rawContent.content || JSON.stringify(rawContent);
  }
  if (typeof rawContent !== 'string') return '[본문 없음]';

  // 줄단위 분리 및 전처리
  let lines = rawContent.split('\n').map(line => line.trim());

  // 본문 '시작점' 찾기
  let startIdx = lines.findIndex(line =>
    /(핵심요약|기사입력|^\d{4}-\d{2}-\d{2}|^[가-힣].{5,50}$)/.test(line)
  );
  if (startIdx < 0) startIdx = 0;

  // 본문 '끝점' 찾기
  let endIdx = lines.findIndex((line, idx) =>
    idx > startIdx &&
    /^(댓글|기자|메일|카카오톡|네이버|포토|오늘의 핫뉴스|실시간 랭킹 뉴스|관련 기사|노컷영상|Copyright|©|※CBS노컷뉴스|^AD$|Advertisements)/i.test(line)
  );
  if (endIdx < 0) endIdx = lines.length;

  // 본문 범위 추출
  lines = lines.slice(startIdx, endIdx);

  // 잡음 패턴 필터
  lines = lines.filter(line =>
    !/^(\*+|=+|[-]{2,}|돌아가기|함께 보면 좋은 콘텐츠|헤드라인 뉴스|Previous|Next|Rate this translation|Your feedback|계속 읽어보세요|Feedback|서울특별시|복사|무단|배포시|추천 뉴스|투표|로그인|서비스 문의|최고의 역사|취소|확인|닫기|챗GPT|성\(性\)적|19금|콘텐츠|포토|랭킹|뉴스브랜드|검색|공익|문화|사업|문의|글자크기|이미지 확대|URL 복사|공유|제공|^AD$|^--+$|Copyright|Advertisements)/i.test(line)
    && line.length > 1
  );

  let txt = lines.join('\n')
    .replace(/!\[.*?\]\(.*?\)/g, '')
    .replace(/\[[^\]]+\]\([^\)]+\)/g, '')
    .replace(/https?:\/\/[^\s)]+/g, '')
    .replace(/[▶▷●◆■★※▲▼→←↑↓]|■|▶/g, '')
    .replace(/[|│]+/g, '');

  txt = txt.replace(/\n{2,}/g, '\n\n');
  txt = txt.replace(/ {2,}/g, ' ');
  txt = txt.trim();

  if (!txt || txt.length < 30) return '[본문 없음]';
  return txt;
}

// 4. Transform Data를 URL 기준으로 맵 생성
const transformMap = new Map();
transformData.forEach(item => {
  const url = item.json.url;
  if (url) {
    transformMap.set(url, item.json);
  }
});

console.log(`Transform Map 크기: ${transformMap.size}`);

// 5. 현재 데이터 처리 및 Transform Data와 병합
let successCount = 0;
let failCount = 0;

const results = items.map((item, index) => {
  const raw = item.json.results?.[0]?.raw_content || '';
  const url = item.json.results?.[0]?.url || '';

  // 본문 정제
  const cleaned = cleanNewsBody(raw);

  if (cleaned !== '[본문 없음]') {
    successCount++;
    console.log(`[${index}] 성공: ${url.substring(0, 50)}... - ${cleaned.length}자`);
  } else {
    failCount++;
    console.log(`[${index}] 실패: ${url.substring(0, 50)}...`);
  }

  // Transform Data에서 메타정보 가져오기
  const transformInfo = transformMap.get(url) || {};

  return {
    json: {
      // Transform Data 정보 (제목, 날짜, 출처 등)
      ...transformInfo,
      // 정제된 본문 추가
      newsBody: cleaned,
      // 원본 URL (중복이지만 명확성을 위해)
      url: url
    }
  };
});

console.log(`\n=== 최종 결과 ===`);
console.log(`정제 성공: ${successCount}개`);
console.log(`정제 실패: ${failCount}개`);
console.log(`총 반환: ${results.length}개`);

return results;

// 'Cleaning Data' 노드에서 모든 아이템 가져오기
const cleaningItems = $('Cleaning Data').all();

// newsBody 필드만 추출
const newsBodies = cleaningItems.map(item => item.json.newsBody);

// 원하는 구분자로 합침 (예: '\n---\n')
const allNewsBody = newsBodies.join('\n---\n');

// 한 개 아이템으로 반환
return [
  {
    json: { allNewsBody }
  }
];

const md = $json["output"]["content"];

// ----------- Markdown Inline 변환 함수 ----------- //
function mdToRichText(text) {
  const patterns = [
    { regex: /\*\*(.*?)\*\*/, annotation: { bold: true } },
    { regex: /\*(.*?)\*/, annotation: { italic: true } },
    { regex: /==(.*?)==/, annotation: { color: "yellow_background" } }
  ];
  let rich = [];
  let rest = text;
  while (rest.length > 0) {
    let minIdx = rest.length;
    let matchObj = null;
    let patternObj = null;
    for (const p of patterns) {
      const m = p.regex.exec(rest);
      if (m && m.index < minIdx) {
        minIdx = m.index;
        matchObj = m;
        patternObj = p;
      }
    }
    if (matchObj) {
      if (matchObj.index > 0) {
        rich.push({ type: 'text', text: { content: rest.slice(0, matchObj.index) } });
      }
      rich.push({
        type: 'text',
        text: { content: matchObj[1] },
        annotations: patternObj.annotation
      });
      rest = rest.slice(matchObj.index + matchObj[0].length);
    } else {
      if (rest.length > 0) rich.push({ type: 'text', text: { content: rest } });
      break;
    }
  }
  return rich;
}

// ----------- 2단계 children만 허용하도록 flatten ----------- //
function safeChildren(children) {
  if (!children) return;
  return children.map(child => {
    if (['bulleted_list_item', 'numbered_list_item'].includes(child.type)) {
      if (child[child.type].children) {
        child[child.type].children = child[child.type].children.map(grandChild => {
          if (grandChild[grandChild.type] && grandChild[grandChild.type].children) {
            delete grandChild[grandChild.type].children;
          }
          return grandChild;
        });
      }
    }
    return child;
  });
}

// ----------- 불릿에 쌍따옴표 여러 개 → children 분리 ----------- //
function splitBulletsWithQuotes(text) {
  const quotePattern = /"([^"]+)"/g;
  let matches = [];
  let match;
  let lastIndex = 0;
  let children = [];
  while ((match = quotePattern.exec(text)) !== null) {
    matches.push(match[1]);
    lastIndex = quotePattern.lastIndex;
  }
  if (matches.length > 1) {
    for (let quoted of matches) {
      children.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: { rich_text: mdToRichText(quoted) }
      });
    }
    let rest = text.slice(lastIndex).replace(/^[,\s]+/, '');
    if (rest.length > 0) {
      children.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: { rich_text: mdToRichText(rest) }
      });
    }
    return children;
  }
  return null;
}

// ----------- 본문 블록 변환 ----------- //
const lines = md.split('\n');
let blocks = [];
let i = 0;

while (i < lines.length) {
  let line = lines[i];

  // 구분선(---)은 무시
  if (line.trim() === '---') {
    i++; continue;
  }

  // Heading
  if (line.startsWith('### ')) {
    blocks.push({ object: 'block', type: 'heading_3', heading_3: { rich_text: mdToRichText(line.replace(/^###\s*/, '')) } });
    i++; continue;
  }
  if (line.startsWith('## ')) {
    blocks.push({ object: 'block', type: 'heading_2', heading_2: { rich_text: mdToRichText(line.replace(/^##\s*/, '')) } });
    i++; continue;
  }
  if (line.startsWith('# ')) {
    blocks.push({ object: 'block', type: 'heading_1', heading_1: { rich_text: mdToRichText(line.replace(/^#\s*/, '')) } });
    i++; continue;
  }

  // 넘버드 리스트 (하위: 불릿/인용/따옴표)
  if (/^\d+\.\s+/.test(line)) {
    let numberListBlocks = [];
    while (i < lines.length && /^\d+\.\s+/.test(lines[i])) {
      let itemText = lines[i].replace(/^\d+\.\s+/, '').trim();
      let children = [];
      let j = i + 1;
      while (
        j < lines.length &&
        (lines[j].trim().startsWith('- ') ||
         lines[j].trim().startsWith('> ') ||
         lines[j].trim().match(/^".*"$/) ||
         lines[j].trim().match(/^“.*”$/))
      ) {
        let childLine = lines[j].trim();
        if (childLine.startsWith('- ')) {
          children.push({
            object: 'block',
            type: 'bulleted_list_item',
            bulleted_list_item: { rich_text: mdToRichText(childLine.replace(/^- /, '').trim()) }
          });
        }
        else if (childLine.startsWith('> ')) {
          children.push({
            object: 'block',
            type: 'quote',
            quote: { rich_text: mdToRichText(childLine.replace(/^> /, '').trim()) }
          });
        }
        else if (childLine.match(/^".*"$/) || childLine.match(/^“.*”$/)) {
          children.push({
            object: 'block',
            type: 'quote',
            quote: { rich_text: mdToRichText(childLine.replace(/^["“]|["”]$/g, '').trim()) }
          });
        }
        j++;
      }
      let block = {
        object: 'block',
        type: 'numbered_list_item',
        numbered_list_item: { rich_text: mdToRichText(itemText) }
      };
      if (children.length > 0) block.numbered_list_item.children = safeChildren(children);
      numberListBlocks.push(block);
      i = j;
    }
    blocks.push(...numberListBlocks);
    continue;
  }

  // 볼드 불릿(상위), children 지원
  if (/^- \*\*(.+?)\*\*/.test(line)) {
    const parentText = line.replace(/^- \*\*(.+?)\*\*/, '$1').trim();
    let parentBlock = {
      object: 'block',
      type: 'bulleted_list_item',
      bulleted_list_item: { rich_text: mdToRichText(parentText), children: [] }
    };
    let j = i + 1;
    while (j < lines.length) {
      let childLine = lines[j];
      if (/^- (?!\*\*)/.test(childLine)) {
        parentBlock.bulleted_list_item.children.push({
          object: 'block',
          type: 'bulleted_list_item',
          bulleted_list_item: { rich_text: mdToRichText(childLine.replace(/^- /, '').trim()) }
        });
        j++; continue;
      }
      if (/^> /.test(childLine)) {
        parentBlock.bulleted_list_item.children.push({
          object: 'block',
          type: 'quote',
          quote: { rich_text: mdToRichText(childLine.replace(/^> /, '').trim()) }
        });
        j++; continue;
      }
      if (/^"/.test(childLine.trim())) {
        parentBlock.bulleted_list_item.children.push({
          object: 'block',
          type: 'quote',
          quote: { rich_text: mdToRichText(childLine.trim().replace(/^"/, '').replace(/"$/, '').trim()) }
        });
        j++; continue;
      }
      if (/^\d+\.\s+/.test(childLine)) {
        parentBlock.bulleted_list_item.children.push({
          object: 'block',
          type: 'numbered_list_item',
          numbered_list_item: { rich_text: mdToRichText(childLine.replace(/^\d+\.\s+/, '').trim()) }
        });
        j++; continue;
      }
      if (
        childLine.trim() === '' ||
        /^- \*\*/.test(childLine) ||
        /^#{1,3} /.test(childLine) ||
        /^\d+\.\s+/.test(childLine)
      ) break;
      parentBlock.bulleted_list_item.children.push({
        object: 'block',
        type: 'paragraph',
        paragraph: { rich_text: mdToRichText(childLine.trim()) }
      });
      j++;
    }
    parentBlock.bulleted_list_item.children = safeChildren(parentBlock.bulleted_list_item.children);
    blocks.push(parentBlock);
    i = j;
    continue;
  }

  // **불릿 계층 구조**
  if (/^- /.test(line.trim())) {
    let parentText = line.trim().replace(/^- /, '');
    // 쌍따옴표 여러 개 → children
    let splitChildren = splitBulletsWithQuotes(parentText);
    if (splitChildren) {
      // 하위 불릿(2칸 이상 들여쓰기)
      let j = i + 1;
      let childBullets = [];
      while (j < lines.length && /^ {2,}- /.test(lines[j])) {
        childBullets.push({
          object: 'block',
          type: 'bulleted_list_item',
          bulleted_list_item: { rich_text: mdToRichText(lines[j].replace(/^ {2,}- /, '')) }
        });
        j++;
      }
      blocks.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: { rich_text: mdToRichText(""), children: safeChildren([...splitChildren, ...childBullets]) }
      });
      i = j;
      continue;
    }
    // **2칸 들여쓰기 하위 불릿만 children**
    let j = i + 1;
    let childBullets = [];
    while (j < lines.length && /^ {2,}- /.test(lines[j])) {
      childBullets.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: { rich_text: mdToRichText(lines[j].replace(/^ {2,}- /, '')) }
      });
      j++;
    }
    let parentBlock = {
      object: 'block',
      type: 'bulleted_list_item',
      bulleted_list_item: { rich_text: mdToRichText(parentText) }
    };
    if (childBullets.length > 0) {
      parentBlock.bulleted_list_item.children = safeChildren(childBullets);
    }
    blocks.push(parentBlock);
    i = j;
    continue;
  }

  // 표
  if (line.trim().startsWith('|')) {
    let tableRows = [];
    while (i < lines.length && lines[i].trim().startsWith('|')) {
      tableRows.push(lines[i].trim());
      i++;
    }
    const rows = tableRows.map(row => row.replace(/^\|/, '').replace(/\|$/, '').split('|').map(cell => cell.trim()));
    const tableBlock = {
      object: 'block',
      type: 'table',
      table: {
        table_width: rows[0].length,
        has_column_header: true,
        has_row_header: false,
        children: rows.slice(2).map(row =>
          ({
            object: 'block',
            type: 'table_row',
            table_row: {
              cells: row.map(cell => mdToRichText(cell))
            }
          })
        )
      }
    };
    blocks.push(tableBlock);
    continue;
  }

  // Callout
  if (line.trim().startsWith('> [!')) {
    const match = line.trim().match(/^\> \[\!(\w+)\] (.*)/);
    const type = match && match[1] ? match[1].toLowerCase() : "info";
    const content = match && match[2] ? match[2] : "";
    const emojiMap = { 'info': 'ℹ️', 'summary': '💡', 'warning': '⚠️' };
    const emoji = emojiMap[type] || '💬';
    let calloutText = content;
    let k = i + 1;
    let calloutChildren = [];
    while (lines[k] && lines[k].trim().startsWith('> -')) {
      calloutChildren.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: { rich_text: mdToRichText(lines[k].trim().replace(/^>\s*-\s*/, '')) }
      });
      k++;
    }
    let block = {
      object: 'block',
      type: 'callout',
      callout: {
        icon: { emoji },
        rich_text: mdToRichText(calloutText),
        color: 'gray_background'
      }
    };
    if (calloutChildren.length > 0)
      block.callout.children = safeChildren(calloutChildren);
    blocks.push(block);
    i = k;
    continue;
  }

  // 인용
  if (line.trim().startsWith('> ')) {
    blocks.push({
      object: 'block',
      type: 'quote',
      quote: { rich_text: mdToRichText(line.trim().replace(/^>\s*/, '')) }
    });
    i++; continue;
  }

  // 인용(공백)
  if (/^ +/.test(line) && line.trim().length > 0) {
    blocks.push({
      object: 'block',
      type: 'quote',
      quote: { rich_text: mdToRichText(line.trim()) }
    });
    i++; continue;
  }

  // 일반 텍스트
  if (line.trim().length > 0) {
    blocks.push({
      object: 'block',
      type: 'paragraph',
      paragraph: { rich_text: mdToRichText(line.trim()) }
    });
    i++; continue;
  }
  i++;
}

return [{ json: { children: blocks } }];

// Cleaning Data의 각 아이템을 개별 처리
const data = $input.item.json;

// 각 기사별로 Notion 블록 배열 생성
const notionBlocks = [];

// Title 헤딩 + 내용
notionBlocks.push({
  object: "block",
  type: "heading_2",
  heading_2: {
    rich_text: [{ type: "text", text: { content: "Title" } }]
  }
});

const titleText = data.title || "제목 없음";
const titleLink = data.url || null;

notionBlocks.push({
  object: "block",
  type: "paragraph",
  paragraph: {
    rich_text: [{ 
      type: "text", 
      text: { 
        content: titleText,
        link: titleLink ? { url: titleLink } : undefined
      }
    }]
  }
});

// Source 헤딩 + 내용
notionBlocks.push({
  object: "block",
  type: "heading_2",
  heading_2: {
    rich_text: [{ type: "text", text: { content: "Source" } }]
  }
});

notionBlocks.push({
  object: "block",
  type: "paragraph",
  paragraph: {
    rich_text: [{ 
      type: "text", 
      text: { 
        content: data.source || "출처 없음" 
      }
    }]
  }
});

// Pubdate 헤딩 + 내용
notionBlocks.push({
  object: "block",
  type: "heading_2",
  heading_2: {
    rich_text: [{ type: "text", text: { content: "Pubdate" } }]
  }
});

notionBlocks.push({
  object: "block",
  type: "paragraph",
  paragraph: {
    rich_text: [{ 
      type: "text", 
      text: { 
        content: data.publishedDateOnly || "날짜 없음" 
      }
    }]
  }
});

// Image 헤딩 + 내용
notionBlocks.push({
  object: "block",
  type: "heading_2",
  heading_2: {
    rich_text: [{ type: "text", text: { content: "Image" } }]
  }
});

if (data.thumbnail && data.thumbnail.trim() !== "") {
  notionBlocks.push({
    object: "block",
    type: "image",
    image: {
      type: "external",
      external: { url: data.thumbnail }
    }
  });
} else {
  notionBlocks.push({
    object: "block",
    type: "paragraph",
    paragraph: {
      rich_text: [{ 
        type: "text", 
        text: { 
          content: "이미지 없음" 
        }
      }]
    }
  });
}

// Content 헤딩 + 내용
notionBlocks.push({
  object: "block",
  type: "heading_2",
  heading_2: {
    rich_text: [{ type: "text", text: { content: "Content" } }]
  }
});

// newsBody 처리
const content = data.newsBody || "";

if (content.trim() === "") {
  notionBlocks.push({
    object: "block",
    type: "paragraph",
    paragraph: {
      rich_text: [{ 
        type: "text", 
        text: { 
          content: "본문 내용 없음" 
        }
      }]
    }
  });
} else {
  // newsBody를 2000자 단위로 분할 (Notion API 제한)
  const chunkSize = 2000;

  for (let i = 0; i < content.length; i += chunkSize) {
    const chunk = content.slice(i, i + chunkSize);

    if (chunk.trim() !== "") {
      notionBlocks.push({
        object: "block",
        type: "paragraph",
        paragraph: {
          rich_text: [{ 
            type: "text", 
            text: { 
              content: chunk 
            }
          }]
        }
      });
    }
  }
}

// 구분선
notionBlocks.push({
  object: "block",
  type: "divider",
  divider: {}
});

// ⭐ notionBlocks만 반환 (원본 데이터 제외)
return {
  json: {
    children: notionBlocks
  }
};
