const blockDomains = ['news.nate.com', 'v.daum.net', 'msn.com'];
const seenUrls = new Set();
const allTransformedNews = [];
const collectedDateTime = new Date();

allSerpResults.forEach((serpItem, index) => {
  const serpResult = serpItem.json;
  const currentKeyword = splitOutItems[index].json.Keywords;

  if (!serpResult.news_results || serpResult.news_results.length === 0) return;

  const allNews = [];
  let globalPosition = 0;

  serpResult.news_results.forEach((group) => {
    if (group.stories && Array.isArray(group.stories)) {
      group.stories.forEach((story) => {
        globalPosition++;
        allNews.push({ ...story, position: globalPosition });
      });
    } else if (group.title) {
      globalPosition++;
      allNews.push({ ...group, position: globalPosition });
    }
  });

  allNews.forEach((news) => {
    const url = news.link;

    if (url && blockDomains.some(domain => url.includes(domain))) return;
    if (!url || seenUrls.has(url)) return;
    seenUrls.add(url);

    let publishedDateOnly = null;

    if (news.date) {
      try {
        const dateStr = news.date.replace(/, \+\d{4} UTC$/, '');
        const parsedDate = new Date(dateStr);
        publishedDateOnly = parsedDate.toISOString().split('T')[0];
      } catch (e) {
        publishedDateOnly = new Date().toISOString().split('T')[0];
      }
    } else {
      publishedDateOnly = new Date().toISOString().split('T')[0];
    }

    const sourceName = news.source?.name || 'Unknown';
    const searchText = `${news.title} ${sourceName}`.toLowerCase();
    let category = 'ê¸°íƒ€';

    if (/ë¬¸í™”|ì—”í„°|entertainment|ì˜í™”|ìŒì•…|ì˜ˆëŠ¥|ë“œë¼ë§ˆ/.test(searchText)) category = 'ë¬¸í™”';
