    else if (/ê¸°ìˆ |tech|ai|ì¸ê³µì§€ëŠ¥|it/.test(searchText)) category = 'ê¸°ìˆ ';
    else if (/ì •ì¹˜|politics|election|ì„ ê±°/.test(searchText)) category = 'ì •ì¹˜';
    else if (/ìŠ¤í¬ì¸ |sports|ì¶•êµ¬|ì•¼êµ¬/.test(searchText)) category = 'ìŠ¤í¬ì¸ ';

    allTransformedNews.push({
      title: news.title || 'Untitled',
      url: url,
      source: sourceName,
      thumbnail: news.thumbnail || news.thumbnail_small || null,
      publishedDateOnly: publishedDateOnly,
