        j++; continue;
      }
      if (/^\d+\.\s+/.test(childLine)) {
        parentBlock.bulleted_list_item.children.push({
          object: 'block',
          type: 'numbered_list_item',
          numbered_list_item: { rich_text: mdToRichText(childLine.replace(/^\d+\.\s+/, '').trim()) }
        });
        j++; continue;
      }
      if (
        childLine.trim() === '' ||
        /^- \*\*/.test(childLine) ||
        /^#{1,3} /.test(childLine) ||
        /^\d+\.\s+/.test(childLine)
      ) break;
      parentBlock.bulleted_list_item.children.push({
        object: 'block',
        type: 'paragraph',
        paragraph: { rich_text: mdToRichText(childLine.trim()) }
      });
      j++;
    }
    parentBlock.bulleted_list_item.children = safeChildren(parentBlock.bulleted_list_item.children);
    blocks.push(parentBlock);
    i = j;
    continue;
  }

  // **ë¶ˆë¦¿ ê³„ì¸µ êµ¬ì¡°**
  if (/^- /.test(line.trim())) {
    let parentText = line.trim().replace(/^- /, '');
    // ìŒë”°ì˜´í‘œ ì—¬ëŸ¬ ê°œ â†’ children
    let splitChildren = splitBulletsWithQuotes(parentText);
    if (splitChildren) {
      // í•˜ìœ„ ë¶ˆë¦¿(2ì¹¸ ì´ìƒ ë“¤ì—¬ì“°ê¸°)
      let j = i + 1;
      let childBullets = [];
      while (j < lines.length && /^ {2,}- /.test(lines[j])) {
        childBullets.push({
          object: 'block',
          type: 'bulleted_list_item',
          bulleted_list_item: { rich_text: mdToRichText(lines[j].replace(/^ {2,}- /, '')) }
        });
        j++;
      }
      blocks.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: { rich_text: mdToRichText(""), children: safeChildren([...splitChildren, ...childBullets]) }
      });
      i = j;
      continue;
    }
    // **2ì¹¸ ë“¤ì—¬ì“°ê¸° í•˜ìœ„ ë¶ˆë¦¿ë§Œ children**
    let j = i + 1;
    let childBullets = [];
    while (j < lines.length && /^ {2,}- /.test(lines[j])) {
      childBullets.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: { rich_text: mdToRichText(lines[j].replace(/^ {2,}- /, '')) }
      });
      j++;
    }
    let parentBlock = {
      object: 'block',
      type: 'bulleted_list_item',
      bulleted_list_item: { rich_text: mdToRichText(parentText) }
    };
    if (childBullets.length > 0) {
      parentBlock.bulleted_list_item.children = safeChildren(childBullets);
    }
    blocks.push(parentBlock);
    i = j;
    continue;
  }

  // í‘œ
  if (line.trim().startsWith('|')) {
    let tableRows = [];
    while (i < lines.length && lines[i].trim().startsWith('|')) {
      tableRows.push(lines[i].trim());
      i++;
    }
    const rows = tableRows.map(row => row.replace(/^\|/, '').replace(/\|$/, '').split('|').map(cell => cell.trim()));
    const tableBlock = {
      object: 'block',
      type: 'table',
      table: {
        table_width: rows[0].length,
        has_column_header: true,
        has_row_header: false,
        children: rows.slice(2).map(row =>
          ({
            object: 'block',
            type: 'table_row',
            table_row: {
              cells: row.map(cell => mdToRichText(cell))
            }
          })
        )
      }
    };
    blocks.push(tableBlock);
    continue;
  }

  // Callout
  if (line.trim().startsWith('> [!')) {
    const match = line.trim().match(/^\> \[\!(\w+)\] (.*)/);
    const type = match && match[1] ? match[1].toLowerCase() : "info";
    const content = match && match[2] ? match[2] : "";
    const emojiMap = { 
      'info': 'â„¹ï¸', 
      'abstract': 'ğŸ“‹',
      'example': 'ğŸ“Œ',
      'question': 'â“',
      'summary': 'ğŸ’¡', 
      'tip': 'âœ…',
      'note': 'ğŸ“',
      'warning': 'âš ï¸' 
    };
    const emoji = emojiMap[type] || 'ğŸ’¬';
    let calloutText = content;
    let k = i + 1;
    let calloutChildren = [];
    while (lines[k] && lines[k].trim().startsWith('> -')) {
      calloutChildren.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: { rich_text: mdToRichText(lines[k].trim().replace(/^>\s*-\s*/, '')) }
      });
      k++;
    }
    let block = {
      object: 'block',
      type: 'callout',
      callout: {
        icon: { emoji },
        rich_text: mdToRichText(calloutText),
        color: 'gray_background'
      }
    };
    if (calloutChildren.length > 0)
      block.callout.children = safeChildren(calloutChildren);
    blocks.push(block);
    i = k;
    continue;
  }

  // ì¸ìš©
  if (line.trim().startsWith('> ')) {
    blocks.push({
      object: 'block',
      type: 'quote',
      quote: { rich_text: mdToRichText(line.trim().replace(/^>\s*/, '')) }
    });
    i++; continue;
  }

  // ì¸ìš©(ê³µë°±)
  if (/^ +/.test(line) && line.trim().length > 0) {
    blocks.push({
      object: 'block',
      type: 'quote',
      quote: { rich_text: mdToRichText(line.trim()) }
    });
    i++; continue;
  }

  // ì¼ë°˜ í…ìŠ¤íŠ¸
  if (line.trim().length > 0) {
    blocks.push({
      object: 'block',
      type: 'paragraph',
      paragraph: { rich_text: mdToRichText(line.trim()) }
    });
    i++; continue;
  }
  i++;
}

return [{ json: { children: blocks } }];
