// 품목구분 양식 노드에서 데이터 가져오기
const formData = $('품목구분 양식').first().json;
const category = formData.category;
const task = formData.task || '';

// 각 품목을 드롭다운 옵션 형식으로 변환
const items = $input.all();
const options = items.map(item => {
  const code = item.json.품목코드 || '';
  const name = item.json['품목명[규격] - 조립'] || '';
  const price = item.json.단가 || 0;
  const stock = item.json.현재수량 || 0;

  return {
    option: `${code} | ${name} | ${price.toLocaleString()}원 | 재고:${stock}개`
  };
});

// 결과 반환
return {
  options: options,
  category: category,
  task: task
};

// ============================================
// 재고 상태 자동 업데이트 + Slack 메시지 생성
// ============================================

const items = $input.all();
const updatedItems = [];
const dangerItems = [];

// 스프레드시트 URL (위험재고 시트)
const SHEET_URL = 'https://docs.google.com/spreadsheets/d/1Np34VXNUdoex-S_795Lj9P0KgpxgRiNfBhKoU0YqyxU/edit#gid=1388800362';

items.forEach(item => {
  const itemCode = item.json['품목코드'];
  const itemName = item.json['품목명[규격] - 조립'];
  const category = item.json['구분 A'];
  const currentStock = parseInt(item.json['현재수량'] || 0);
  const dangerLevel = parseInt(item.json['위험재고'] || 0);

  // 상태 판단 로직
  let status = '';
  let statusEmoji = '';
  let statusColor = '';

  if (currentStock > dangerLevel * 1.5) {
    // 안전: 위험재고의 150% 초과
    status = '안전';
    statusEmoji = '🟢';
    statusColor = 'green';
  } else if (currentStock > dangerLevel) {
    // 주의: 위험재고 초과하지만 150% 이하
    status = '주의';
    statusEmoji = '🟡';
    statusColor = 'orange';
  } else {
    // 위험: 위험재고 이하 (마이너스 포함)
    status = '위험';
    statusEmoji = '🔴';
    statusColor = 'red';
  }

  // 재고 차이 계산
  const stockDifference = currentStock - dangerLevel;
  const stockPercentage = dangerLevel > 0 ? 
    ((currentStock / dangerLevel) * 100).toFixed(1) : 
    0;

  const itemData = {
    '품목코드': itemCode,
    '품목명[규격] - 조립': itemName,
    '구분 A': category,
    '현재수량': currentStock,
    '위험재고': dangerLevel,
    '상태': `${statusEmoji} ${status}`,
    '상태값': status,
    '상태색상': statusColor,
    '재고차이': stockDifference,
    '재고비율': `${stockPercentage}%`
  };

  updatedItems.push(itemData);

  // 위험 품목 수집
  if (status === '위험') {
    dangerItems.push(itemData);
  }
});

// ============================================
// Slack 메시지 생성
// ============================================

let slackMessage = '';

if (dangerItems.length > 0) {
  const timestamp = new Date().toLocaleString('ko-KR', {
    timeZone: 'Asia/Seoul',
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit'
  });

  slackMessage = `🚨 *재고 위험 알림*\n\n`;
  slackMessage += `⏰ ${timestamp}\n\n`;
  slackMessage += `━━━━━━━━━━━━━━━━━━━━\n\n`;
  slackMessage += `🔴 *위험 품목: ${dangerItems.length}개*\n\n`;

  dangerItems.forEach((item, index) => {
    const shortage = item['위험재고'] - item['현재수량'];

    slackMessage += `${index + 1}. *${item['품목명[규격] - 조립']}* (${item['품목코드']})\n`;
    slackMessage += `    현재: ${item['현재수량']}개 | 위험: ${item['위험재고']}개\n`;
    slackMessage += `    ⚠️ ${shortage}개 부족\n\n`;
  });

  slackMessage += `━━━━━━━━━━━━━━━━━━━━\n\n`;
  slackMessage += `💡 위 품목들에 대한 *즉시 발주*가 필요합니다.\n`;
  slackMessage += `📋 자세한 내용은 <${SHEET_URL}|위험재고 시트>를 확인하세요.`;
}

// ============================================
// 결과 반환
// ============================================

return updatedItems.map(item => ({
  ...item,
  'Slack메시지': slackMessage,
  '위험품목수': dangerItems.length,
  '알림필요': dangerItems.length > 0
}));

// 일별 데이터를 월별로 집계
const dailyData = $input.all();
const monthlyMap = new Map();

dailyData.forEach(item => {
  const date = item.json['거래일자']; // "2025-11-03"
  const yearMonth = date.substring(0, 7); // "2025-11"

  const itemCode = item.json['품목코드'];
  const itemName = item.json['품목명[규격] - 조립'];

  const key = `${yearMonth}_${itemCode}`;

  if (!monthlyMap.has(key)) {
    monthlyMap.set(key, {
      '거래월분': yearMonth,
      '품목코드': itemCode,
      '품목명[규격] - 조립': itemName,
      '월별 입고': 0,
      '월별 생산': 0,
      '월별 출고': 0
    });
  }

  const summary = monthlyMap.get(key);
  summary['월별 입고'] += parseInt(item.json['입고'] || 0);
  summary['월별 생산'] += parseInt(item.json['생산'] || 0);
  summary['월별 출고'] += parseInt(item.json['출고'] || 0);
});

return Array.from(monthlyMap.values());
