// 네이버 블로그 API 응답 중복 제거 + 정제 + 모바일 URL 변환

const movie = $('Notion Trigger').item.json.properties.Movie.title[0].plain_text;
const keywordPageId = $('Notion Trigger').item.json.id;
const splitOutItems = $('Blog Split Out').all();
const allNaverResults = $input.all();

// KST 시간 추출
let collectedAtKST = '';
if (allNaverResults.length > 0 && allNaverResults[0].json.lastBuildDate) {
  try {
    const buildDate = new Date(allNaverResults[0].json.lastBuildDate);
    if (!isNaN(buildDate.getTime())) {
      const kstDate = new Date(buildDate.getTime() + (9 * 60 * 60 * 1000));
      collectedAtKST = kstDate.toISOString().slice(0, 16).replace('T', ' ');
    } else {
      throw new Error('Invalid date');
    }
  } catch {
    const now = new Date();
    const kstTime = new Date(now.getTime() + (9 * 60 * 60 * 1000));
    collectedAtKST = kstTime.toISOString().slice(0, 16).replace('T', ' ');
  }
} else {
  const now = new Date();
  const kstTime = new Date(now.getTime() + (9 * 60 * 60 * 1000));
  collectedAtKST = kstTime.toISOString().slice(0, 16).replace('T', ' ');
}

const seenUrls = new Set();
const allTransformedBlogs = [];
let globalCounter = 1;

// HTML 태그 제거
const stripHtml = (html) => {
  if (!html) return '';
  return html
    .replace(/<[^>]*>/g, '')
    .replace(/&quot;/g, '"')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&nbsp;/g, ' ')
    .replace(/&#39;/g, "'")
    .trim();
};

// 날짜 포맷팅
const formatDate = (dateStr) => {
  if (!dateStr || dateStr.length !== 8) {
    return new Date().toISOString().split('T')[0];
  }
  return `${dateStr.slice(0, 4)}-${dateStr.slice(4, 6)}-${dateStr.slice(6, 8)}`;
};

// URL 정규화
const normalizeUrl = (url) => {
  if (!url) return '';
  return url.toLowerCase().replace(/\/$/, '').replace(/\?.*$/, '');
};

// 블로그 정보 추출
const extractBlogInfo = (url) => {
  if (!url) return { blogId: null, logNo: null };

  if (url.includes('PostView.naver') || url.includes('blogId=')) {
    const blogIdMatch = url.match(/[?&]blogId=([^&]+)/);
    const logNoMatch = url.match(/[?&]logNo=(\d+)/);
    if (blogIdMatch && logNoMatch) {
      return { blogId: blogIdMatch[1], logNo: logNoMatch[1] };
    }
  }

  const match1 = url.match(/blog\.naver\.com\/([^/\?]+)\/(\d+)/);
  if (match1) return { blogId: match1[1], logNo: match1[2] };

  const match2 = url.match(/m\.blog\.naver\.com\/([^/\?]+)\/(\d+)/);
  if (match2) return { blogId: match2[1], logNo: match2[2] };

  return { blogId: null, logNo: null };
};

// 모바일 URL 생성
const createMobileUrl = (url) => {
  const { blogId, logNo } = extractBlogInfo(url);
  return (blogId && logNo) ? `https://m.blog.naver.com/${blogId}/${logNo}` : url;
};

// 네이버 API 응답 처리
for (let index = 0; index < allNaverResults.length; index++) {
  const resultItem = allNaverResults[index];
  const splitData = splitOutItems[index]?.json || {};
  const currentKeyword = splitData.Keywords || '알 수 없음';
  const naverResult = resultItem.json;

  if (!naverResult.items || naverResult.items.length === 0) continue;

  for (let position = 0; position < naverResult.items.length; position++) {
    const blog = naverResult.items[position];
    const url = blog.link;
    const normalizedUrl = normalizeUrl(url);

    if (!url || seenUrls.has(normalizedUrl)) continue;
    seenUrls.add(normalizedUrl);

    const publishedDateOnly = formatDate(blog.postdate);
    let bloggerLinkFull = blog.bloggerlink;
    if (bloggerLinkFull && !bloggerLinkFull.startsWith('http')) {
      bloggerLinkFull = 'https://' + bloggerLinkFull;
    }

    const mobileUrl = createMobileUrl(url);
    const { blogId, logNo } = extractBlogInfo(url);

    allTransformedBlogs.push({
      blogNumber: globalCounter++,
      title: stripHtml(blog.title),
      description: stripHtml(blog.description),
      url: url,
      mobileUrl: mobileUrl,
      blogId: blogId,
      logNo: logNo,
      bloggerName: blog.bloggername || '알 수 없음',
      bloggerLink: bloggerLinkFull,
      publishedDateOnly: publishedDateOnly,
      collectedAt: collectedAtKST,
      position: position + 1,
      keyword: currentKeyword,
      movie: movie,
      keywordPageId: keywordPageId
    });
  }
}

return allTransformedBlogs.map(item => ({ json: item }));
