const md = $json["output"]["content"];

// ----------- Markdown Inline ë³€í™˜ í•¨ìˆ˜ (ë§í¬ ì§€ì› ì¶”ê°€) ----------- //
function mdToRichText(text) {
  const patterns = [
    { regex: /\[([^\]]+)\]\(([^)]+)\)/, type: 'link' }, // ë§í¬ ìš°ì„  ì²˜ë¦¬
    { regex: /\*\*(.*?)\*\*/, annotation: { bold: true } },
    { regex: /\*(.*?)\*/, annotation: { italic: true } },
    { regex: /==(.*?)==/, annotation: { color: "yellow_background" } }
  ];

  let rich = [];
  let rest = text;

  while (rest.length > 0) {
    let minIdx = rest.length;
    let matchObj = null;
    let patternObj = null;

    for (const p of patterns) {
      const m = p.regex.exec(rest);
      if (m && m.index < minIdx) {
        minIdx = m.index;
        matchObj = m;
        patternObj = p;
      }
    }

    if (matchObj) {
      if (matchObj.index > 0) {
        rich.push({ type: 'text', text: { content: rest.slice(0, matchObj.index) } });
      }

      // ë§í¬ ì²˜ë¦¬
      if (patternObj.type === 'link') {
        rich.push({
          type: 'text',
          text: { content: matchObj[1], link: { url: matchObj[2] } }
        });
      } else {
        rich.push({
          type: 'text',
          text: { content: matchObj[1] },
          annotations: patternObj.annotation
        });
      }

      rest = rest.slice(matchObj.index + matchObj[0].length);
    } else {
      if (rest.length > 0) rich.push({ type: 'text', text: { content: rest } });
      break;
    }
  }

  return rich;
}

// ----------- 2ë‹¨ê³„ childrenë§Œ í—ˆìš©í•˜ë„ë¡ flatten ----------- //
function safeChildren(children) {
  if (!children) return;
  return children.map(child => {
    if (['bulleted_list_item', 'numbered_list_item'].includes(child.type)) {
      if (child[child.type].children) {
        child[child.type].children = child[child.type].children.map(grandChild => {
          if (grandChild[grandChild.type] && grandChild[grandChild.type].children) {
            delete grandChild[grandChild.type].children;
          }
          return grandChild;
        });
      }
    }
    return child;
  });
}

// ----------- ë¶ˆë¦¿ì— ìŒë”°ì˜´í‘œ ì—¬ëŸ¬ ê°œ â†’ children ë¶„ë¦¬ ----------- //
function splitBulletsWithQuotes(text) {
  const quotePattern = /"([^"]+)"/g;
  let matches = [];
  let match;
  let lastIndex = 0;
  let children = [];

  while ((match = quotePattern.exec(text)) !== null) {
    matches.push(match[1]);
    lastIndex = quotePattern.lastIndex;
  }

  if (matches.length > 1) {
    for (let quoted of matches) {
      children.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: { rich_text: mdToRichText(quoted) }
      });
    }
    let rest = text.slice(lastIndex).replace(/^[,\s]+/, '');
    if (rest.length > 0) {
      children.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: { rich_text: mdToRichText(rest) }
      });
    }
    return children;
  }

  return null;
}

// ----------- ë³¸ë¬¸ ë¸”ë¡ ë³€í™˜ ----------- //
const lines = md.split('\n');
let blocks = [];
let i = 0;

while (i < lines.length) {
  let line = lines[i];

  // êµ¬ë¶„ì„ (---) â†’ Divider
  if (line.trim() === '---') {
    blocks.push({
      object: 'block',
      type: 'divider',
      divider: {}
    });
    i++;
    continue;
  }

  // â­ ì´ë¯¸ì§€ ì²˜ë¦¬ (ê°€ì¥ ì¤‘ìš”!)
  if (line.trim().startsWith('![')) {
    const match = line.trim().match(/!\[([^\]]*)\]\(([^)]+)\)/);
    if (match) {
      const url = match[2].trim();
      blocks.push({
        object: 'block',
        type: 'image',
        image: {
          type: 'external',
          external: { url: url }
        }
      });
      i++;
      continue;
    }
  }

  // ì½”ë“œ ë¸”ë¡ ì²˜ë¦¬
  if (line.trim().startsWith('```')) {
    const lang = line.trim().replace(/^```/, '').trim() || 'plain text';
    let codeLines = [];
    let j = i + 1;
    while (j < lines.length && !lines[j].trim().startsWith('```')) {
      codeLines.push(lines[j]);
      j++;
    }
    const langMap = {
      'json': 'json',
      'javascript': 'javascript',
      'python': 'python',
      'markdown': 'markdown'
    };
    blocks.push({
      object: 'block',
      type: 'code',
      code: {
        rich_text: [{ type: 'text', text: { content: codeLines.join('\n') } }],
        language: langMap[lang] || 'plain text'
      }
    });
    i = j + 1; // skip closing ```
    continue;
  }

  // Heading 3 (ìƒ‰ìƒ íƒœê·¸ ì œê±°)
  if (line.startsWith('### ')) {
    let headingText = line.replace(/^###\s*/, '');
    headingText = headingText.replace(/<font[^>]*>(.*?)<\/font>/g, '$1');
    blocks.push({
      object: 'block',
      type: 'heading_3',
      heading_3: { rich_text: mdToRichText(headingText) }
    });
    i++;
    continue;
  }

  // Heading 2 (ìƒ‰ìƒ íƒœê·¸ ì œê±° + ì˜¤ë Œì§€ ìƒ‰ìƒ)
  if (line.startsWith('## ')) {
    let headingText = line.replace(/^##\s*/, '');
    headingText = headingText.replace(/<font[^>]*>(.*?)<\/font>/g, '$1');
    blocks.push({
      object: 'block',
      type: 'heading_2',
      heading_2: {
        rich_text: mdToRichText(headingText),
        color: 'orange'
      }
    });
    i++;
    continue;
  }

  // Heading 1 (ìƒ‰ìƒ íƒœê·¸ ì œê±°)
  if (line.startsWith('# ')) {
    let headingText = line.replace(/^#\s*/, '');
    headingText = headingText.replace(/<font[^>]*>(.*?)<\/font>/g, '$1');
    blocks.push({
      object: 'block',
      type: 'heading_1',
      heading_1: { rich_text: mdToRichText(headingText) }
    });
    i++;
    continue;
  }

  // ë„˜ë²„ë“œ ë¦¬ìŠ¤íŠ¸ (í•˜ìœ„: ë¶ˆë¦¿/ì¸ìš©/ë”°ì˜´í‘œ)
  if (/^\d+\.\s+/.test(line)) {
    let numberListBlocks = [];
    while (i < lines.length && /^\d+\.\s+/.test(lines[i])) {
      let itemText = lines[i].replace(/^\d+\.\s+/, '').trim();
      let children = [];
      let j = i + 1;

      while (
        j < lines.length &&
        (lines[j].trim().startsWith('- ') ||
         lines[j].trim().startsWith('> ') ||
         lines[j].trim().match(/^".*"$/) ||
         lines[j].trim().match(/^".*"$/))
      ) {
        let childLine = lines[j].trim();
        if (childLine.startsWith('- ')) {
          children.push({
            object: 'block',
            type: 'bulleted_list_item',
            bulleted_list_item: { rich_text: mdToRichText(childLine.replace(/^- /, '').trim()) }
          });
        }
        else if (childLine.startsWith('> ')) {
          children.push({
            object: 'block',
            type: 'quote',
            quote: { rich_text: mdToRichText(childLine.replace(/^> /, '').trim()) }
          });
        }
        else if (childLine.match(/^".*"$/) || childLine.match(/^".*"$/)) {
          children.push({
            object: 'block',
            type: 'quote',
            quote: { rich_text: mdToRichText(childLine.replace(/^[""]|[""]$/g, '').trim()) }
          });
        }
        j++;
      }

      let block = {
        object: 'block',
        type: 'numbered_list_item',
        numbered_list_item: { rich_text: mdToRichText(itemText) }
      };

      if (children.length > 0) {
        block.numbered_list_item.children = safeChildren(children);
      }

      numberListBlocks.push(block);
      i = j;
    }
    blocks.push(...numberListBlocks);
    continue;
  }

  // ë³¼ë“œ ë¶ˆë¦¿ (ìƒìœ„), children ì§€ì›
  if (/^- \*\*(.+?)\*\*/.test(line)) {
    const parentText = line.replace(/^- \*\*(.+?)\*\*/, '$1').trim();
    let parentBlock = {
      object: 'block',
      type: 'bulleted_list_item',
      bulleted_list_item: { rich_text: mdToRichText(parentText), children: [] }
    };

    let j = i + 1;
    while (j < lines.length) {
      let childLine = lines[j];

      if (/^- (?!\*\*)/.test(childLine)) {
        parentBlock.bulleted_list_item.children.push({
          object: 'block',
          type: 'bulleted_list_item',
          bulleted_list_item: { rich_text: mdToRichText(childLine.replace(/^- /, '').trim()) }
        });
        j++;
        continue;
      }

      if (/^> /.test(childLine)) {
        parentBlock.bulleted_list_item.children.push({
          object: 'block',
          type: 'quote',
          quote: { rich_text: mdToRichText(childLine.replace(/^> /, '').trim()) }
        });
        j++;
        continue;
      }

      if (/^"/.test(childLine.trim())) {
        parentBlock.bulleted_list_item.children.push({
          object: 'block',
          type: 'quote',
          quote: { rich_text: mdToRichText(childLine.trim().replace(/^"/, '').replace(/"$/, '').trim()) }
        });
        j++;
        continue;
      }

      if (/^\d+\.\s+/.test(childLine)) {
        parentBlock.bulleted_list_item.children.push({
          object: 'block',
          type: 'numbered_list_item',
          numbered_list_item: { rich_text: mdToRichText(childLine.replace(/^\d+\.\s+/, '').trim()) }
        });
        j++;
        continue;
      }

      if (
        childLine.trim() === '' ||
        /^- \*\*/.test(childLine) ||
        /^#{1,3} /.test(childLine) ||
        /^\d+\.\s+/.test(childLine)
      ) break;

      parentBlock.bulleted_list_item.children.push({
        object: 'block',
        type: 'paragraph',
        paragraph: { rich_text: mdToRichText(childLine.trim()) }
      });
      j++;
    }

    parentBlock.bulleted_list_item.children = safeChildren(parentBlock.bulleted_list_item.children);
    blocks.push(parentBlock);
    i = j;
    continue;
  }

  // ë¶ˆë¦¿ ê³„ì¸µ êµ¬ì¡°
  if (/^- /.test(line.trim())) {
    let parentText = line.trim().replace(/^- /, '');

    // ìŒë”°ì˜´í‘œ ì—¬ëŸ¬ ê°œ â†’ children
    let splitChildren = splitBulletsWithQuotes(parentText);
    if (splitChildren) {
      // í•˜ìœ„ ë¶ˆë¦¿ (2ì¹¸ ì´ìƒ ë“¤ì—¬ì“°ê¸°)
      let j = i + 1;
      let childBullets = [];
      while (j < lines.length && /^ {2,}- /.test(lines[j])) {
        childBullets.push({
          object: 'block',
          type: 'bulleted_list_item',
          bulleted_list_item: { rich_text: mdToRichText(lines[j].replace(/^ {2,}- /, '')) }
        });
        j++;
      }
      blocks.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: {
          rich_text: mdToRichText(""),
          children: safeChildren([...splitChildren, ...childBullets])
        }
      });
      i = j;
      continue;
    }

    // 2ì¹¸ ë“¤ì—¬ì“°ê¸° í•˜ìœ„ ë¶ˆë¦¿ë§Œ children
    let j = i + 1;
    let childBullets = [];
    while (j < lines.length && /^ {2,}- /.test(lines[j])) {
      childBullets.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: { rich_text: mdToRichText(lines[j].replace(/^ {2,}- /, '')) }
      });
      j++;
    }

    let parentBlock = {
      object: 'block',
      type: 'bulleted_list_item',
      bulleted_list_item: { rich_text: mdToRichText(parentText) }
    };

    if (childBullets.length > 0) {
      parentBlock.bulleted_list_item.children = safeChildren(childBullets);
    }

    blocks.push(parentBlock);
    i = j;
    continue;
  }

  // í‘œ
  if (line.trim().startsWith('|')) {
    let tableRows = [];
    while (i < lines.length && lines[i].trim().startsWith('|')) {
      tableRows.push(lines[i].trim());
      i++;
    }

    const rows = tableRows.map(row =>
      row.replace(/^\|/, '').replace(/\|$/, '').split('|').map(cell => cell.trim())
    );

    // êµ¬ë¶„ì„ (ë‘ ë²ˆì§¸ ì¤„) ì œê±°
    const dataRows = rows.filter((row, idx) => idx !== 1);

    const tableBlock = {
      object: 'block',
      type: 'table',
      table: {
        table_width: dataRows[0].length,
        has_column_header: true,
        has_row_header: false,
        children: dataRows.slice(1).map(row => ({
          object: 'block',
          type: 'table_row',
          table_row: {
            cells: row.map(cell => mdToRichText(cell))
          }
        }))
      }
    };

    blocks.push(tableBlock);
    continue;
  }

  // Callout
  if (line.trim().startsWith('> [!')) {
    const match = line.trim().match(/^\> \[\!(\w+)\] (.*)/);
    const type = match && match[1] ? match[1].toLowerCase() : "info";
    const content = match && match[2] ? match[2] : "";
    const emojiMap = {
