  cleaned = cleaned.replace(/[!?]{3,}/g, (match) => match[0] + match[0]);

  // 7. ë§ˆì¹¨í‘œ ì—¬ëŸ¬ê°œ ì œê±° (...... -> ...)
  cleaned = cleaned.replace(/\.{4,}/g, '...');

  // 8. ì—°ì† ê³µë°± ì œê±°
  cleaned = cleaned.replace(/\s{2,}/g, ' ');

