// ----------- HTML íƒœê·¸ ì œê±° í•¨ìˆ˜ ----------- //
function stripHtmlTags(text) {
  return text.replace(/<font[^>]*>|<\/font>/gi, '').trim();
}

// ----------- Markdown Inline ë³€í™˜ í•¨ìˆ˜ ----------- //
function mdToRichText(text) {
  const patterns = [
    { regex: /\*\*(.*?)\*\*/, annotation: { bold: true } },
    { regex: /\*(.*?)\*/, annotation: { italic: true } },
    { regex: /==(.*?)==/, annotation: { color: "yellow_background" } }
  ];
  let rich = [];
  let rest = text;
  while (rest.length > 0) {
    let minIdx = rest.length;
    let matchObj = null;
    let patternObj = null;
    for (const p of patterns) {
      const m = p.regex.exec(rest);
      if (m && m.index < minIdx) {
        minIdx = m.index;
        matchObj = m;
        patternObj = p;
      }
    }
    if (matchObj) {
      if (matchObj.index > 0) {
        rich.push({ type: 'text', text: { content: rest.slice(0, matchObj.index) } });
      }
      rich.push({
        type: 'text',
        text: { content: matchObj[1] },
        annotations: patternObj.annotation
      });
      rest = rest.slice(matchObj.index + matchObj[0].length);
